    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}{{ product.name }}{% endblock %}

{% block content %}

    <div class=\"row mt-5\">
        <div class=\"col-md-5 mt-5\">
            <img src=\"/uploads/{{ product.illustration }}\" alt=\"{{ product.name }}\" class=\"img-fluid\">
        </div>
        <div class=\"col-md-7 my-auto\">
            <h1>{{ product.name}}</h1>
            <p>{{ product.subtitle }}</p>
            <span class=\"product-price font-weight-bold\"> {{ (product.price / 100)|number_format(2, ',', '.') }} €</span>
            <hr>
            <p>{{ product.description }} </p>
            <a href=\"{{ path('app_add_to_cart', {'id' : product.id }) }}\" class=\"btn btn-primary\">Ajouter au panier</a>
        </div>

    </div>
    <hr>
    <div class=\"row\">
    <h3 class=\" col-md-12\">Jetez un oeil sur nos produits à la Une ! </h3>
      {% for product in products %}
        <div class=\"col-md-4\">
          <div class=\"product-item text-center \">
                        <a href=\"{{ path('app_product', { 'slug' : product.slug }) }}\"><img src=\"/uploads/{{ product.illustration }}\" alt=\"{{ product.name }}\" class=\"figure-img img-fluid img-thumbnail\" style=\"width: 10rem; height: 10rem\"></a>
                        <h4>{{ product.name }}</h4>
                        <span class=\"product-subtitle\">{{ product.subtitle }}</span>
          </div>
        </div>
      {% endfor %}
    </div>

{% endblock %}

", "product/show.html.twig", "C:\\wamp64\\www\\el-cafexito\\templates\\product\\show.html.twig");
